package com.pixel.org;

public class Pixel {
	public int x;
	public int y;
	public int index;

	public Pixel() {
		super();
		// TODO Auto-generated constructor stub
	}

	public Pixel(int x, int y, int index) {
		super();
		this.x = x;
		this.y = y;
		this.index = index;
	}

	public int getX() {
		return x;
	}

	public void setX(int x) {
		this.x = x;
	}

	public int getY() {
		return y;
	}

	public void setY(int y) {
		this.y = y;
	}

	public int getIndex() {
		return index;
	}

	public void setIndex(int index) {
		this.index = index;
	}

}
